.gradient-primary {
  background: hsl(220.9, 39.3%, 11%);
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
}

.gradient-accent {
  background: hsl(217.2, 91.2%, 59.8%);
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
}

.gradient-success {
  background: hsl(142, 76%, 36%);
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
}

/* Clean Card Backgrounds */
.gradient-card {
  background: hsl(0, 0%, 100%);
  border: 1px solid hsl(220, 13%, 91%);
}

/* Clean effect for panels */
.glass-panel {
  background: hsl(0, 0%, 100%);
  border: 1px solid hsl(220, 13%, 91%);
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
}

/* High contrast mode for accessibility */
@media (prefers-contrast: high) {
  :root {
    --border: hsl(0, 0%, 0%);
    --ring: hsl(0, 0%, 0%);
  }

  .theme-minimal {
    --border: hsl(0, 0%, 0%);
    --ring: hsl(0, 0%, 0%);
  }
}

/* Reduced motion for accessibility */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* Sidebar Explicit Styling - Ensure solid backgrounds */
[data-testid="sidebar-main"],
.bg-sidebar,
[data-sidebar="sidebar"] {
  background-color: #ffffff !important; /* White background */
  border-right: 1px solid #e2e8f0 !important; /* Light gray border */
}

.theme-minimal [data-testid="sidebar-main"],
.theme-minimal .bg-sidebar,
.theme-minimal [data-sidebar="sidebar"] {
  background-color: #ffffff !important; /* White background for minimal theme */
  border-right: 1px solid #d1d5db !important; /* Minimal theme border */
}

/* Sidebar text colors */
.text-sidebar-foreground {
  color: hsl(220, 90%, 12%) !important;
}

.theme-minimal .text-sidebar-foreground {
  color: hsl(220, 90%, 10%) !important;
}

/* Sidebar accent colors for hover states */
.bg-sidebar-accent {
  background-color: hsl(240, 50%, 92%) !important;
}

.theme-minimal .bg-sidebar-accent {
  background-color: hsl(220, 20%, 90%) !important;
}

/* Ensure main content offset for sidebar */
.peer-data-\[state\=expanded\]\:ml-\[var\(--sidebar-width\)\] {
  margin-left: 16rem !important;
}

/* Dark mode support (if needed later) */
@media (prefers-color-scheme: dark) {
  .auto-theme {
    --background: hsl(0, 0%, 0%);
    --foreground: hsl(0, 0%, 95%);
    --card: hsl(0, 0%, 5%);
    --card-foreground: hsl(0, 0%, 95%);
  }
}

