import OpenAI from "openai";

// the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key"
});

export interface GeneratedQuestion {
  question: string;
  type: "mcq" | "true_false" | "short_answer" | "poll";
  options?: string[];
  correctAnswer: string;
  explanation?: string;
  difficulty: "easy" | "medium" | "hard";
  source?: string;
  category?: string;
}

export interface QuizGenerationOptions {
  questionTypes: string[];
  questionCount: number;
  difficulty: "easy" | "medium" | "hard" | "mixed";
  language: string;
  topic?: string;
}

export class AIQuizGenerator {
  async generateFromText(content: string, options: QuizGenerationOptions): Promise<GeneratedQuestion[]> {
    try {
      const prompt = this.buildPrompt(content, options);

      const response = await openai.chat.completions.create({
        model: "gpt-5",
        messages: [
          {
            role: "system",
            content: "You are an expert quiz creator. Generate educational quiz questions based on the provided content. Always respond with valid JSON in the specified format."
          },
          {
            role: "user",
            content: prompt
          }
        ],
        response_format: { type: "json_object" },
        temperature: 0.7,
      });

      const result = JSON.parse(response.choices[0].message.content || "{}");

      if (!result.questions || !Array.isArray(result.questions)) {
        throw new Error("Invalid response format from AI");
      }

      return this.validateAndCleanQuestions(result.questions);
    } catch (error) {
      console.error("AI Quiz Generation Error:", error);
      throw new Error("Failed to generate quiz questions: " + (error as Error).message);
    }
  }

  async generateFromURL(url: string, options: QuizGenerationOptions): Promise<GeneratedQuestion[]> {
    try {
      // Extract content from URL
      const content = await this.extractContentFromURL(url);
      return this.generateFromText(content, options);
    } catch (error) {
      console.error("URL Content Extraction Error:", error);
      throw new Error("Failed to extract content from URL: " + (error as Error).message);
    }
  }

  private buildPrompt(content: string, options: QuizGenerationOptions): string {
    const questionTypesText = options.questionTypes.join(", ");

    return `
Create ${options.questionCount} quiz questions based on the following content. 

Content:
${content}

Requirements:
- Question types: ${questionTypesText}
- Difficulty: ${options.difficulty}
- Language: ${options.language}
${options.topic ? `- Focus on topic: ${options.topic}` : ''}

For each question, provide:
1. Clear, concise question text
2. Question type (mcq, true_false, short_answer, or poll)
3. For MCQ: 4 options with one correct answer
4. For True/False: boolean answer
5. For Short Answer: expected answer phrase
6. Brief explanation of the correct answer
7. Difficulty level (easy/medium/hard)
8. Source reference (page number, section, or URL)
9. Category/topic classification

Ensure questions are:
- Educational and meaningful
- Appropriate for the target difficulty
- Diverse in topics covered
- Free from bias or offensive content
- Factually accurate

Respond in JSON format:
{
  "questions": [
    {
      "question": "Question text here",
      "type": "mcq",
      "options": ["Option 1", "Option 2", "Option 3", "Option 4"],
      "correctAnswer": "Option 1",
      "explanation": "Brief explanation",
      "difficulty": "medium",
      "source": "Source reference",
      "category": "Topic category"
    }
  ]
}
`;
  }

  private normalizeURL(url: string): string {
    // Trim whitespace
    url = url.trim();

    // Add protocol if missing
    if (!url.match(/^https?:\/\//)) {
      // Try HTTPS first, fallback to HTTP if needed
      url = 'https://' + url;
    }

    try {
      // Validate URL format
      new URL(url);
      return url;
    } catch (error) {
      throw new Error(`Invalid URL format: ${url}`);
    }
  }

  private async extractContentFromURL(url: string): Promise<string> {
    try {
      const normalizedURL = this.normalizeURL(url);

      const response = await fetch(normalizedURL, {
        headers: {
          'User-Agent': 'Qurious Quiz Generator Bot 1.0'
        },
        redirect: 'follow'
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const html = await response.text();

      // Simple HTML text extraction (in production, use a proper HTML parser)
      const textContent = html
        .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
        .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
        .replace(/<[^>]*>/g, ' ')
        .replace(/\s+/g, ' ')
        .trim();

      // Limit content length
      return textContent.substring(0, 8000);
    } catch (error) {
      throw new Error(`Failed to fetch URL content: ${(error as Error).message}`);
    }
  }

  private validateAndCleanQuestions(questions: any[]): GeneratedQuestion[] {
    return questions
      .filter(q => q.question && q.type && q.correctAnswer)
      .map(q => ({
        question: q.question.trim(),
        type: q.type,
        options: q.type === "mcq" ? (q.options || []) : undefined,
        correctAnswer: q.correctAnswer.trim(),
        explanation: q.explanation?.trim(),
        difficulty: q.difficulty || "medium",
        source: q.source?.trim(),
        category: q.category?.trim()
      }))
      .slice(0, 25); // Limit to max 25 questions
  }

  async generateFromPDF(buffer: Buffer, options: QuizGenerationOptions): Promise<GeneratedQuestion[]> {
    // This would require a PDF parsing library like pdf-parse
    // For now, throw an error indicating it needs implementation
    throw new Error("PDF parsing not yet implemented. Please use text input or URL instead.");
  }
}

export const aiQuizGenerator = new AIQuizGenerator();

server/services/websocket.ts.content:
import { WebSocketServer, WebSocket } from "ws";
import { type Server } from "http";
import { storage } from "../storage";
import { type Participant } from "@shared/schema";

interface WebSocketMessage {
  type: string;
  sessionId?: string;
  participantId?: string;
  data?: any;
}

interface SessionConnection {
  sessionId: string;
  isHost: boolean;
  participantId?: string;
  userId?: string;
}

class WebSocketManager {
  private wss: WebSocketServer | null = null;
  private connections: Map<WebSocket, SessionConnection> = new Map();
  private sessionConnections: Map<string, Set<WebSocket>> = new Map();

  init(server: Server) {
    this.wss = new WebSocketServer({ server, path: '/ws' });

    this.wss.on('connection', (ws) => {
      console.log('New WebSocket connection');

      ws.on('message', async (data) => {
        try {
          const message: WebSocketMessage = JSON.parse(data.toString());
          await this.handleMessage(ws, message);
        } catch (error) {
          console.error('WebSocket message error:', error);
          this.sendError(ws, 'Invalid message format');
        }
      });

      ws.on('close', () => {
        this.handleDisconnection(ws);
      });

      ws.on('error', (error) => {
        console.error('WebSocket error:', error);
        this.handleDisconnection(ws);
      });
    });
  }

  private async handleMessage(ws: WebSocket, message: WebSocketMessage) {
    switch (message.type) {
      case 'join_session':
        await this.handleJoinSession(ws, message);
        break;
      case 'host_session':
        await this.handleHostSession(ws, message);
        break;
      case 'participant_answer':
        await this.handleParticipantAnswer(ws, message);
        break;
      case 'host_next_question':
        await this.handleNextQuestion(ws, message);
        break;
      case 'host_start_session':
        await this.handleStartSession(ws, message);
        break;
      case 'host_pause_session':
        await this.handlePauseSession(ws, message);
        break;
      case 'host_end_session':
        await this.handleEndSession(ws, message);
        break;
      default:
        this.sendError(ws, 'Unknown message type');
    }
  }

  private async handleJoinSession(ws: WebSocket, message: WebSocketMessage) {
    const { sessionCode, nickname } = message.data;

    try {
      const session = await storage.getSessionByCode(sessionCode);
      if (!session) {
        this.sendError(ws, 'Session not found');
        return;
      }

      if (session.status === 'completed') {
        this.sendError(ws, 'Session has ended');
        return;
      }

      const participantId = `participant_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      const participant: Participant = {
        id: participantId,
        nickname: nickname,
        joinedAt: new Date().toISOString(),
        isReady: true,
        score: 0,
        streak: 0,
      };

      // Add participant to session
      const currentParticipants = Array.isArray(session.participants) ? session.participants : [];
      currentParticipants.push(participant);

      await storage.updateSession(session.id, {
        participants: currentParticipants
      });

      // Store connection info
      this.connections.set(ws, {
        sessionId: session.id,
        isHost: false,
        participantId: participantId
      });

      // Add to session connections
      if (!this.sessionConnections.has(session.id)) {
        this.sessionConnections.set(session.id, new Set());
      }
      this.sessionConnections.get(session.id)!.add(ws);

      // Notify participant
      this.sendMessage(ws, {
        type: 'join_success',
        data: {
          sessionId: session.id,
          participantId: participantId,
          quiz: await storage.getQuiz(session.quizId)
        }
      });

      // Notify all connections in session about new participant
      this.broadcastToSession(session.id, {
        type: 'participant_joined',
        data: {
          participant: participant,
          totalParticipants: currentParticipants.length
        }
      });

    } catch (error) {
      console.error('Join session error:', error);
      this.sendError(ws, 'Failed to join session');
    }
  }

  private async handleHostSession(ws: WebSocket, message: WebSocketMessage) {
    const { sessionId, userId } = message.data;

    try {
      const session = await storage.getSession(sessionId);
      if (!session) {
        this.sendError(ws, 'Session not found');
        return;
      }

      if (session.hostId !== userId) {
        this.sendError(ws, 'Unauthorized');
        return;
      }

      // Store connection info
      this.connections.set(ws, {
        sessionId: session.id,
        isHost: true,
        userId: userId
      });

      // Add to session connections
      if (!this.sessionConnections.has(session.id)) {
        this.sessionConnections.set(session.id, new Set());
      }
      this.sessionConnections.get(session.id)!.add(ws);

      // Send current session state
      this.sendMessage(ws, {
        type: 'host_connected',
        data: {
          session: session,
          participants: session.participants || []
        }
      });

    } catch (error) {
      console.error('Host session error:', error);
      this.sendError(ws, 'Failed to connect as host');
    }
  }

  private async handleParticipantAnswer(ws: WebSocket, message: WebSocketMessage) {
    const connection = this.connections.get(ws);
    if (!connection || connection.isHost) {
      this.sendError(ws, 'Unauthorized');
      return;
    }

    const { questionIndex, answer, responseTime } = message.data;

    try {
      const session = await storage.getSession(connection.sessionId);
      if (!session) {
        this.sendError(ws, 'Session not found');
        return;
      }

      const quiz = await storage.getQuiz(session.quizId);
      if (!quiz) {
        this.sendError(ws, 'Quiz not found');
        return;
      }

      const questions = Array.isArray(quiz.questions) ? quiz.questions : [];
      const currentQuestion = questions[questionIndex];

      if (!currentQuestion) {
        this.sendError(ws, 'Invalid question index');
        return;
      }

      // Calculate if answer is correct
      const isCorrect = answer === currentQuestion.correctAnswer;

      // Calculate points (example scoring system)
      let points = 0;
      if (isCorrect) {
        const timeBonus = Math.max(0, 1000 - responseTime);
        points = 500 + timeBonus;
      }

      // Update participant streak
      const participants = Array.isArray(session.participants) ? session.participants : [];
      const participantIndex = participants.findIndex(p => p.id === connection.participantId);

      if (participantIndex >= 0) {
        const participant = participants[participantIndex];
        participant.score += points;
        participant.streak = isCorrect ? participant.streak + 1 : 0;

        await storage.updateSession(session.id, { participants });
      }

      // Store response
      await storage.createResponse({
        sessionId: connection.sessionId,
        participantId: connection.participantId!,
        questionIndex: questionIndex,
        answer: answer,
        isCorrect: isCorrect,
        responseTime: responseTime,
        points: points,
        streak: participants[participantIndex]?.streak || 0
      });

      // Send response confirmation to participant
      this.sendMessage(ws, {
        type: 'answer_received',
        data: {
          isCorrect: isCorrect,
          points: points,
          streak: participants[participantIndex]?.streak || 0
        }
      });

      // Notify host of response
      this.broadcastToSessionHosts(connection.sessionId, {
        type: 'participant_answered',
        data: {
          participantId: connection.participantId,
          questionIndex: questionIndex,
          totalResponses: await this.getResponseCount(connection.sessionId, questionIndex)
        }
      });

    } catch (error) {
      console.error('Participant answer error:', error);
      this.sendError(ws, 'Failed to process answer');
    }
  }

  private async handleNextQuestion(ws: WebSocket, message: WebSocketMessage) {
    const connection = this.connections.get(ws);
    if (!connection || !connection.isHost) {
      this.sendError(ws, 'Unauthorized');
      return;
    }

    try {
      const session = await storage.getSession(connection.sessionId);
      if (!session) {
        this.sendError(ws, 'Session not found');
        return;
      }

      const nextQuestionIndex = session.currentQuestionIndex + 1;

      await storage.updateSession(session.id, {
        currentQuestionIndex: nextQuestionIndex
      });

      // Broadcast next question to all participants
      this.broadcastToSession(connection.sessionId, {
        type: 'next_question',
        data: {
          questionIndex: nextQuestionIndex
        }
      });

    } catch (error) {
      console.error('Next question error:', error);
      this.sendError(ws, 'Failed to advance question');
    }
  }

  private async handleStartSession(ws: WebSocket, message: WebSocketMessage) {
    const connection = this.connections.get(ws);
    if (!connection || !connection.isHost) {
      this.sendError(ws, 'Unauthorized');
      return;
    }

    try {
      await storage.updateSession(connection.sessionId, {
        status: 'active',
        startedAt: new Date()
      });

      this.broadcastToSession(connection.sessionId, {
        type: 'session_started',
        data: {}
      });

    } catch (error) {
      console.error('Start session error:', error);
      this.sendError(ws, 'Failed to start session');
    }
  }

  private async handlePauseSession(ws: WebSocket, message: WebSocketMessage) {
    const connection = this.connections.get(ws);
    if (!connection || !connection.isHost) {
      this.sendError(ws, 'Unauthorized');
      return;
    }

    try {
      await storage.updateSession(connection.sessionId, {
        status: 'paused'
      });

      this.broadcastToSession(connection.sessionId, {
        type: 'session_paused',
        data: {}
      });

    } catch (error) {
      console.error('Pause session error:', error);
      this.sendError(ws, 'Failed to pause session');
    }
  }

  private async handleEndSession(ws: WebSocket, message: WebSocketMessage) {
    const connection = this.connections.get(ws);
    if (!connection || !connection.isHost) {
      this.sendError(ws, 'Unauthorized');
      return;
    }

    try {
      await storage.updateSession(connection.sessionId, {
        status: 'completed',
        completedAt: new Date()
      });

      this.broadcastToSession(connection.sessionId, {
        type: 'session_ended',
        data: {}
      });

      // Clean up connections
      this.cleanupSession(connection.sessionId);

    } catch (error) {
      console.error('End session error:', error);
      this.sendError(ws, 'Failed to end session');
    }
  }

  private handleDisconnection(ws: WebSocket) {
    const connection = this.connections.get(ws);
    if (connection) {
      // Remove from session connections
      const sessionConnections = this.sessionConnections.get(connection.sessionId);
      if (sessionConnections) {
        sessionConnections.delete(ws);

        // If participant disconnected, notify others
        if (!connection.isHost && connection.participantId) {
          this.broadcastToSession(connection.sessionId, {
            type: 'participant_left',
            data: {
              participantId: connection.participantId
            }
          });
        }
      }

      this.connections.delete(ws);
    }
  }

  private sendMessage(ws: WebSocket, message: any) {
    if (ws.readyState === WebSocket.OPEN) {
      ws.send(JSON.stringify(message));
    }
  }

  private sendError(ws: WebSocket, error: string) {
    this.sendMessage(ws, {
      type: 'error',
      data: { message: error }
    });
  }

  private broadcastToSession(sessionId: string, message: any) {
    const connections = this.sessionConnections.get(sessionId);
    if (connections) {
      connections.forEach(ws => {
        this.sendMessage(ws, message);
      });
    }
  }

  private broadcastToSessionHosts(sessionId: string, message: any) {
    const connections = this.sessionConnections.get(sessionId);
    if (connections) {
      connections.forEach(ws => {
        const connection = this.connections.get(ws);
        if (connection && connection.isHost) {
          this.sendMessage(ws, message);
        }
      });
    }
  }

  private async getResponseCount(sessionId: string, questionIndex: number): Promise<number> {
    const responses = await storage.getResponsesBySession(sessionId);
    return responses.filter(r => r.questionIndex === questionIndex).length;
  }

  private cleanupSession(sessionId: string) {
    const connections = this.sessionConnections.get(sessionId);
    if (connections) {
      connections.forEach(ws => {
        this.connections.delete(ws);
      });
      this.sessionConnections.delete(sessionId);
    }
  }
}

export const webSocketManager = new WebSocketManager();

client/src/components/confetti.tsx.content:
import { useEffect, useState } from "react";

interface ConfettiProps {
  active: boolean;
  onComplete?: () => void;
}

interface ConfettiPiece {
  id: number;
  left: number;
  delay: number;
  duration: number;
  color: string;
}

const colors = [
  "hsl(262, 83%, 58%)", // primary
  "hsl(25, 95%, 53%)",  // accent
  "hsl(142, 76%, 36%)", // success
  "hsl(38, 92%, 50%)"   // warning
];

export function Confetti({ active, onComplete }: ConfettiProps) {
  const [pieces, setPieces] = useState<ConfettiPiece[]>([]);

  useEffect(() => {
    if (!active) {
      setPieces([]);
      return;
    }

    const newPieces: ConfettiPiece[] = [];

    for (let i = 0; i < 50; i++) {
      newPieces.push({
        id: i,
        left: Math.random() * 100,
        delay: Math.random() * 2,
        duration: Math.random() * 2 + 1,
        color: colors[Math.floor(Math.random() * colors.length)]
      });
    }

    setPieces(newPieces);

    // Clean up after animation
    const timeout = setTimeout(() => {
      setPieces([]);
      onComplete?.();
    }, 3000);

    return () => clearTimeout(timeout);
  }, [active, onComplete]);

  if (!active || pieces.length === 0) {
    return null;
  }

  return (
    <div className="fixed inset-0 pointer-events-none z-50">
      {pieces.map((piece) => (
        <div
          key={piece.id}
          className="confetti-piece absolute"
          style={{
            left: `${piece.left}vw`,
            backgroundColor: piece.color,
            animationDelay: `${piece.delay}s`,
            animationDuration: `${piece.duration}s`
          }}
        />
      ))}
    </div>
  );
}

