import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-session.ts.content:
import { useState, useCallback } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useWebSocket } from "./use-websocket";
import { useToast } from "./use-toast";
import { apiRequest } from "@/lib/queryClient";
import { type Session, type Participant, type GameSettings } from "@shared/schema";

export function useSession() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [sessionId, setSessionId] = useState<string>("");
  const [participantId, setParticipantId] = useState<string>("");
  const [isHost, setIsHost] = useState(false);

  const { subscribe, send, isConnected } = useWebSocket();

  // Get session details
  const { data: sessionData, isLoading } = useQuery({
    queryKey: ["/api/session", sessionId],
    queryFn: async () => {
      if (!sessionId) return null;
      const response = await fetch(`/api/session/${sessionId}`);
      if (!response.ok) throw new Error("Failed to fetch session");
      return response.json();
    },
    enabled: !!sessionId
  });

  const session: Session | null = sessionData?.session || null;

  // Create session mutation
  const createSessionMutation = useMutation({
    mutationFn: async (data: {
      quizId: string;
      hostId: string;
      settings: GameSettings;
    }) => {
      const response = await apiRequest("POST", "/api/session", data);
      return response.json();
    },
    onSuccess: (data) => {
      setSessionId(data.session.id);
      setIsHost(true);
      queryClient.invalidateQueries({ queryKey: ["/api/session", data.session.id] });

      if (isConnected) {
        send({
          type: "host_session",
          data: {
            sessionId: data.session.id,
            userId: data.session.hostId
          }
        });
      }
    },
    onError: (error) => {
      toast({
        title: "Failed to Create Session",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  // Join session function
  const joinSession = useCallback(async (sessionCode: string, nickname: string) => {
    try {
      const response = await fetch(`/api/session/code/${sessionCode}`);
      if (!response.ok) throw new Error("Session not found");

      const data = await response.json();
      setSessionId(data.session.id);
      setIsHost(false);

      if (isConnected) {
        send({
          type: "join_session",
          data: { sessionCode, nickname }
        });
      }

      return data.session;
    } catch (error) {
      toast({
        title: "Failed to Join Session",
        description: error instanceof Error ? error.message : "Unknown error",
        variant: "destructive"
      });
      throw error;
    }
  }, [send, isConnected, toast]);

  // Session control functions
  const startSession = useCallback(() => {
    if (!sessionId || !isConnected || !isHost) return;

    send({
      type: "host_start_session",
      data: { sessionId }
    });
  }, [sessionId, isConnected, isHost, send]);

  const pauseSession = useCallback(() => {
    if (!sessionId || !isConnected || !isHost) return;

    send({
      type: "host_pause_session", 
      data: { sessionId }
    });
  }, [sessionId, isConnected, isHost, send]);

  const nextQuestion = useCallback((questionIndex: number) => {
    if (!sessionId || !isConnected || !isHost) return;

    send({
      type: "host_next_question",
      data: { sessionId, questionIndex }
    });
  }, [sessionId, isConnected, isHost, send]);

  const endSession = useCallback(() => {
    if (!sessionId || !isConnected || !isHost) return;

    send({
      type: "host_end_session",
      data: { sessionId }
    });
  }, [sessionId, isConnected, isHost, send]);

  const submitAnswer = useCallback((questionIndex: number, answer: string, responseTime: number) => {
    if (!sessionId || !isConnected || isHost) return;

    send({
      type: "participant_answer",
      data: { questionIndex, answer, responseTime }
    });
  }, [sessionId, isConnected, isHost, send]);

  return {
    // State
    session,
    sessionId,
    participantId,
    isHost,
    isLoading,
    isConnected,

    // Mutations
    createSession: createSessionMutation.mutate,
    isCreatingSession: createSessionMutation.isPending,

    // Functions
    joinSession,
    startSession,
    pauseSession,
    nextQuestion,
    endSession,
    submitAnswer,

    // WebSocket utilities
    subscribe,
    send,

    // Setters (for manual state management if needed)
    setSessionId,
    setParticipantId,
    setIsHost
  };
}

client/src/hooks/use-theme.ts.content:
import * as React from "react";

export type Theme = "playful" | "minimal";

export function useTheme() {
  const [theme, setTheme] = React.useState<Theme>("playful");

  React.useEffect(() => {
    // Load theme from localStorage
    const savedTheme = localStorage.getItem("qurious-theme") as Theme;
    if (savedTheme && (savedTheme === "playful" || savedTheme === "minimal")) {
      setTheme(savedTheme);
      if (savedTheme === "minimal") {
        document.body.classList.add("theme-minimal");
      }
    }
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === "playful" ? "minimal" : "playful";
    setTheme(newTheme);
    localStorage.setItem("qurious-theme", newTheme);

    if (newTheme === "minimal") {
      document.body.classList.add("theme-minimal");
    } else {
      document.body.classList.remove("theme-minimal");
    }
  };

  return {
    theme,
    toggleTheme,
    isPlayful: theme === "playful",
    isMinimal: theme === "minimal"
  };
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

